$traits = $jsonString | ConvertFrom-Json


# Initialize an array to hold unused traits
$unusedTraits = @()

foreach ($trait in $traits) {
    # Construct the ripgrep command

    $rgCommand = "rg --fixed-strings --quiet -g '*.dm' -g '!_traits.dm' -g '!declarations.dm' $trait code monkestation/code"

    # Run the ripgrep command
    Invoke-Expression $rgCommand

    # Check the exit code to determine if the trait was found
    if ($LASTEXITCODE -ne 0) {
        $unusedTraits += $trait
    }
}

# Print out the unused traits
if ($unusedTraits.Count -eq 0) {
    Write-Output "All traits are in use."
} else {
    Write-Output "The following traits are not in use:"
    $unusedTraits | ForEach-Object { Write-Output $_ }
}
